import React, { useMemo } from 'react';
import { useWallet } from 'use-wallet';
import moment from 'moment';
import styled from 'styled-components';
import Spacer from '../../components/Spacer';
import Harvest from './components/Harvest';
import Stake from './components/Stake';
import { makeStyles } from '@material-ui/core/styles';

import { Box, Card, CardContent, Button, Typography, Grid } from '@material-ui/core';

import { Alert } from '@material-ui/lab';

import UnlockWallet from '../../components/UnlockWallet';
import Page from '../../components/Page';

import useModal from '../../hooks/useModal';
import useRedeemOnMasonry from '../../hooks/useRedeemOnMasonry';
import useAllocateSeigniorage from '../../hooks/useAllocateSeigniorage';
import useStakedBalanceOnMasonry from '../../hooks/useStakedBalanceOnMasonry';
import { getDisplayBalance } from '../../utils/formatBalance';
import useCurrentEpoch from '../../hooks/useCurrentEpoch';
import useFetchMasonryAPR from '../../hooks/useFetchMasonryAPR';

import useCashPriceInEstimatedTWAP from '../../hooks/useCashPriceInEstimatedTWAP';
import useTreasuryAllocationTimes from '../../hooks/useTreasuryAllocationTimes';
import useTotalStakedOnMasonry from '../../hooks/useTotalStakedOnMasonry';
import useClaimRewardCheck from '../../hooks/masonry/useClaimRewardCheck';
import useWithdrawCheck from '../../hooks/masonry/useWithdrawCheck';
import ProgressCountdown from './components/ProgressCountdown';
import ClaimModal from './components/ClaimModal';
import MasonryImage from '../../assets/img/masonry.png';
import { createGlobalStyle } from 'styled-components';

const BackgroundImage = createGlobalStyle`
  body {
    // background-size: cover !important;
    background-color: #131127;
  }
`;

const useStyles = makeStyles((theme) => ({
  gridItem: {
    height: '100%',
    [theme.breakpoints.up('md')]: {
      height: '90px',
    },
  },
}));

const Boardroom = () => {
  const classes = useStyles();
  const { account } = useWallet();
  const { onRedeem } = useRedeemOnMasonry();
  const { onAllocateSeigniorage } = useAllocateSeigniorage();
  const stakedBalance = useStakedBalanceOnMasonry();
  const currentEpoch = useCurrentEpoch();
  const cashStat = useCashPriceInEstimatedTWAP();
  const totalStaked = useTotalStakedOnMasonry();
  const masonryAPR = useFetchMasonryAPR();
  const canClaimReward = useClaimRewardCheck();
  const canWithdraw = useWithdrawCheck();
  const scalingFactor = useMemo(() => (cashStat ? Number(cashStat.priceInDollars).toFixed(4) : null), [cashStat]);
  const { to } = useTreasuryAllocationTimes();



  const [onClaimAction, onDismissAction] = useModal(
    <ClaimModal
      onConfirm={() => {
        onRedeem();
        onDismissAction();
      }}
    />,
  );


  return (
    <Page>
      <BackgroundImage />

      {!!account ? (
        <>
          <Typography style={{color:'#fff', paddingLeft: '15px'}} align="center" variant="h5">
            Board Room
          </Typography>
          <Typography align="center" style={{color:'#fff !important', fontSize: '14px',paddingLeft: '15px'}}>Earn rewards by staking your ASHARE's.</Typography>

          <Box mt={5}>
            <Grid container justify="center" spacing={3}>
              <Grid item xs={6} md={2} lg={2} className={classes.gridItem}>
                <Card className={classes.gridItem}>
                  <CardContent style={{ textAlign: 'center' }}>
                    <Typography style={{marginTop: '5px', color:'#fff !important'}}>Next Epoch</Typography>
                    <ProgressCountdown  style={{fontWeight: 'bold', color: '#fff !important'}} base={moment().toDate()} hideBar={true} deadline={to} description="Next Epoch" />
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={6} md={2} lg={2} className={classes.gridItem}>
                <Card className={classes.gridItem}>
                  <CardContent align="center">
                    <Typography style={{marginTop: '5px', color:'#fff !important'}} align="center" gutterBottom>Current Epoch</Typography>
                    <Typography style={{fontWeight: 'bold', marginTop: '-5px'}}>{Number(currentEpoch)}</Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={12} md={2} lg={2} className={classes.gridItem}>
                <Card className={classes.gridItem}>
                  <CardContent align="center">
                    <Typography style={{marginTop: '5px', color:'#fff !important'}}>
                      APEX Price<small>(TWAP)</small>
                    </Typography>
                    <Typography style={{fontWeight: 'bold'}}>{scalingFactor}</Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={12} md={2} lg={2} className={classes.gridItem}>
                <Card className={classes.gridItem}>
                  <CardContent align="center">
                    <Typography style={{marginTop: '5px', color:'#fff !important'}}>APR</Typography>
                    <Typography style={{fontWeight: 'bold'}}>{masonryAPR.toFixed(2)}%</Typography>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={12} md={2} lg={2}>
                <Card className={classes.gridItem}>
                  <CardContent align="center">
                    <Typography style={{marginTop: '5px', color:'#fff !important'}}>ASHARES Staked</Typography>
                    <Typography style={{fontWeight: 'bold'}}>{getDisplayBalance(totalStaked)}</Typography>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            <Grid container justify="center">
              <Box mt={3} style={{ width: '600px' }}>
                <Alert variant="filled" severity="warning">
                  Staked ASHAREs can only be withdrawn after 6 epochs since deposit.
                </Alert>
              </Box>
            </Grid>

            <Box mt={4}>
              <StyledBoardroom>
                <StyledCardsWrapper>
                  <StyledCardWrapper>
                    <Harvest />
                  </StyledCardWrapper>
                  <Spacer />
                  <StyledCardWrapper>
                    <Stake />
                  </StyledCardWrapper>
                </StyledCardsWrapper>
              </StyledBoardroom>
            </Box>

            {/* <Grid container justify="center" spacing={3}>
            <Grid item xs={4}>
              <Card>
                <CardContent align="center">
                  <Typography>Rewards</Typography>

                </CardContent>
                <CardActions style={{justifyContent: 'center'}}>
                  <Button color="primary" variant="outlined">Claim Reward</Button>
                </CardActions>
                <CardContent align="center">
                  <Typography>Claim Countdown</Typography>
                  <Typography>00:00:00</Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={4}>
              <Card>
                <CardContent align="center">
                  <Typography>Stakings</Typography>
                  <Typography>{getDisplayBalance(stakedBalance)}</Typography>
                </CardContent>
                <CardActions style={{justifyContent: 'center'}}>
                  <Button>+</Button>
                  <Button>-</Button>
                </CardActions>
              </Card>
            </Grid>
          </Grid> */}
          </Box>

          <Box mt={5}>
            <Grid container justify="center" spacing={3} mt={10}>
              <Grid item xs={3} style={{textAlign: 'center'}}>
                <Button
                  disabled={stakedBalance.eq(0) || (!canWithdraw && !canClaimReward)}
                  onClick={onClaimAction}
                  color="primary"
                  variant="contained"
                >
                  Claim and Withdraw
                </Button>
              </Grid>
              <Grid item xs={3} style={{textAlign: 'center'}}>
                <Button
                  disabled={stakedBalance.eq(0)}
                  onClick={onAllocateSeigniorage}
                  color="primary"
                  variant="contained"
                >
                  Push Epoch
                </Button>
              </Grid>
            </Grid>
          </Box>
        </>
      ) : (
        <UnlockWallet />
      )}
    </Page>
  );
};

const StyledBoardroom = styled.div`
  align-items: center;
